<node BACKGROUND_COLOR="#c2c04e" CREATED="1268153812510" FOLDED="true" ID="Freemind_Link_1909361960" MODIFIED="1268846400189" TEXT="Matching surfaces between zones" VSHIFT="-1">
<edge COLOR="#808080" WIDTH="thin"/>
<node CREATED="1269320637945" FOLDED="true" ID="Freemind_Link_536109767" MODIFIED="1269320750928" TEXT="Select the edges (but not the face) that make up the surface you wan to copy">
<node CREATED="1269320704412" ID="Freemind_Link_265099444" MODIFIED="1269320725240" TEXT="If you keep the face selected as well you will end up with face normals reversed"/>
<node CREATED="1269320729906" ID="Freemind_Link_695074832" MODIFIED="1269320743357" TEXT="You can fix this via a right click/ reverse faces, but it is better to avoid it."/>
</node>
<node CREATED="1268264216312" ID="Freemind_Link_35904899" MODIFIED="1269320696064" TEXT="Copy the selected edges"/>
<node CREATED="1268264285671" ID="Freemind_Link_1215854633" MODIFIED="1268264306593" TEXT="Go into the second zone, and paste in place"/>
<node CREATED="1268264307500" ID="Freemind_Link_193415601" MODIFIED="1268264318359" TEXT="Select the zones you want to match up"/>
<node CREATED="1268264318796" ID="Freemind_Link_492651011" LINK="#Freemind_Link_1580103610" MODIFIED="1269320913334" TEXT="Run Surface Matching Tool"/>
<node CREATED="1268264479593" ID="Freemind_Link_1390589639" MODIFIED="1268264509890" TEXT="You can run this as you make connections, or near the end of your modeling"/>
